`) `a` order by `a`.`LEVEL`,`a`.`ID`;

create view dept_view as select `a`.`ID` AS `ID`,`a`.`PID` AS `PID`,`a`.`LEVEL` AS `LEVEL`,`a`.`DEPT_NAME` AS `DEPT_NAME`,`a`.`DEPT_CODE` AS `DEPT_CODE`,`a`.`PLAN_AREA` AS `PLAN_AREA`,`a`.`DESIGN_AREA` AS `DESIGN_AREA`,`a`.`PLAN_AREA_PERSENT` AS `PLAN_AREA_PERSENT`,`a`.`DESIGN_AREA_PERSENT` AS `DESIGN_AREA_PERSENT`,`a`.`NOTE` AS `NOTE`,`a`.`order_idx` AS `order_idx`,`a`.`proj_id` AS `proj_id` from (select `hdtool`.`dept_type`.`ID` AS `ID`,'0' AS `PID`,'1' AS `LEVEL`,`hdtool`.`dept_type`.`DEPT_NAME` AS `DEPT_NAME`,`hdtool`.`dept_type`.`DEPT_CODE` AS `DEPT_CODE`,`hdtool`.`dept_type`.`PLAN_AREA` AS `PLAN_AREA`,`hdtool`.`dept_type`.`DESIGN_AREA` AS `DESIGN_AREA`,`hdtool`.`dept_type`.`PLAN_AREA_PERSENT` AS `PLAN_AREA_PERSENT`,`hdtool`.`dept_type`.`DESIGN_AREA_PERSENT` AS `DESIGN_AREA_PERSENT`,`hdtool`.`dept_type`.`NOTE` AS `NOTE`,`hdtool`.`dept_type`.`ORDER_IDX` AS `order_idx`,`hdtool`.`dept_type`.`PROJ_ID` AS `proj_id` from `hdtool`.`dept_type` union all select `hdtool`.`department`.`ID` AS `ID`,`hdtool`.`department`.`DEPT_TYPE_ID` AS `PID`,'2' AS `LEVEL`,`hdtool`.`department`.`DEPT_NAME` AS `DEPT_NAME`,`hdtool`.`department`.`DEPT_CODE` AS `DEPT_CODE`,`hdtool`.`department`.`PLAN_AREA` AS `PLAN_AREA`,`hdtool`.`department`.`DESIGN_AREA` AS `DESIGN_AREA`,`hdtool`.`department`.`PLAN_AREA_PERSENT` AS `PLAN_AREA_PERSENT`,`hdtool`.`department`.`DESIGN_AREA_PERSENT` AS `DESIGN_AREA_PERSENT`,`hdtool`.`department`.`NOTE` AS `NOTE`,`hdtool`.`department`.`ORDER_IDX` AS `order_idx`,`hdtool`.`department`.`PROJ_ID` AS `proj_id` from `hdtool`.`department`) `a` order by `a`.`PID`,`a`.`ID`;

create view sm_sys_dict as select `a`.`TYPE_CODE` AS `TYPE_CODE`,`a`.`PARAM_CODE` AS `PARAM_CODE`,`a`.`PARAM_DESC` AS `PARAM_DESC`,`a`.`PARENT_TYPE_CODE` AS `PARENT_TYPE_CODE`,`a`.`PARENT_PARAM_CODE` AS `PARENT_PARAM_CODE`,`a`.`LANGUAGE_TYPE` AS `LANGUAGE_TYPE` from (select `hdtool`.`sm_param_detail`.`TYPE_CODE` AS `TYPE_CODE`,`hdtool`.`sm_param_detail`.`PARAM_CODE` AS `PARAM_CODE`,`hdtool`.`sm_param_detail`.`PARAM_DESC` AS `PARAM_DESC`,`hdtool`.`sm_param_detail`.`PARENT_TYPE_CODE` AS `PARENT_TYPE_CODE`,`hdtool`.`sm_param_detail`.`PARENT_PARAM_CODE` AS `PARENT_PARAM_CODE`,`hdtool`.`sm_param_detail`.`LANGUAGE_TYPE` AS `LANGUAGE_TYPE` from `hdtool`.`sm_param_detail` where (`hdtool`.`sm_param_detail`.`STATUS` = 'Y')) `a` order by `a`.`TYPE_CODE`,`a`.`PARAM_CODE`;	