mapper.select:
select ID,
      DESIGN_AREA_TOTAL AS PLAN_AREA
  from area_view
 where 1=1
   and level = #{level,jdbcType=INTEGER}

    and dept_id = #{deptId,jdbcType=BIGINT}


    and pid = #{areaId,jdbcType=BIGINT}

 order by ID

select ID
from area_info
where dept_id = #{deptId,jdbcType=BIGINT}

select max(order_idx)
from area_info
where dept_id = #{deptId,jdbcType=BIGINT}
